import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-registro-alumno',
  templateUrl: './registro-alumno.page.html',
  styleUrls: ['./registro-alumno.page.scss'],
})
export class RegistroAlumnoPage implements OnInit {

  showInfo: boolean = false;

  toggleCard() {
    this.showInfo = !this.showInfo;
  }

  constructor() { }

  ngOnInit() {
  }

}
<ion-content class="ion-padding">
  <div class="logo-container">
    <ion-img src="../../../assets/RegisterAPPlogo.svg" alt="Logo de la aplicación"></ion-img>
  </div>
    <ion-card>
      <ion-card-header (click)="toggleCard()">
        <ion-card-title>Información</ion-card-title>
      </ion-card-header>
      <ion-card-content *ngIf="showInfo">
        <p>Fecha
          <ion-datetime displayFormat="DD/MM/YYYY" min="2020" max="2021" [(ngModel)]="fecha"></ion-datetime>
        </p>
      </ion-card-content>
    </ion-card>
</ion-content>
