import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  constructor(private navCtrl: NavController) {}

  openUserProfile() {
    this.navCtrl.navigateForward('/perfil-usuario');
  }
}
<ion-content class="ion-padding">
  <div class="home-title">
    <ion-toolbar class="welcome-toolbar">
    <ion-title class="welcome-title">Bienvenido pepito</ion-title>
    <ion-buttons slot="end">
      <ion-button (click)="openUserProfile()">
        <ion-icon class="icon-user" name="person-circle"></ion-icon>
      </ion-button>
    </ion-buttons>
  </ion-toolbar>
  </div>
  
  <div class="logo-container">
    <ion-img src="../../../assets/RegisterAPPlogo.svg" alt="Logo de la aplicación"></ion-img>
  </div>
    <ion-button routerLink="/#" class="boton-login" expand="block"  type="submit">Registra tu asistencia</ion-button>
    <ion-button routerLink="/#" class="boton-login" expand="block"  type="submit">Asistencias</ion-button>

    <p class="ion-text-register-forget">
      <a class="help-text" routerLink="/#">¿Necesitas ayuda?</a>
    </p>
</ion-content>
